import React, { useCallback, useState } from "react";
import PropTypes from "prop-types";
import Link from "next/link";
import styled from "styled-components";
import { Button, Form, Input } from "antd";

const LoginForm = ({ setIsLoggedIn }) => {
  const [inputs, setInputs] = useState({ uId: "", password: "" });

  const onChange = useCallback((e) => {
    const { name, value } = e.target;
    setInputs({
      [name]: value,
    });
  });

  const onSubmit = useCallback(() => {
    setIsLoggedIn(true);
  }, []);

  //! css
  const ButtonStyle = styled.div`
    margin-top: 10px;
  `;

  return (
    <Form onFinish={onSubmit}>
      <div>
        <label htmlFor="uId">아이디</label>
        <Input name="uId" value={inputs.uId} onChange={onChange}></Input>
        <label htmlFor="password">비밀번호</label>
        <Input name="password" value={inputs.password} onChange={onChange} required></Input>
      </div>

      <ButtonStyle>
        <Button type="primary" htmlType="submit">
          로그인
        </Button>
        <Link href="/signup">
          <a>
            <Button>회원가입</Button>
          </a>
        </Link>
      </ButtonStyle>
    </Form>
  );
};

LoginForm.propTypes = {
  setIsLoggedIn: PropTypes.elementType.isRequired,
};

export default LoginForm;
